import React, { useRef, useState } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import { Link as Page, useHistory} from 'react-router-dom';
import axios from 'axios';
import { Alert } from '@material-ui/lab';



const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
  h: {
    color: '#000000'
},
}));


export default function SignIn() {
  const classes = useStyles();
  const [error, setError] = useState(false);
  let email = useRef<HTMLInputElement>(null);
  const password = useRef<HTMLInputElement>(null);
  let history = useHistory();
  let errorDiv;

  const getValues = async() => {
    let body = {email: email?.current?.value, password: password?.current?.value};
    const responce = await axios.post('http://localhost:8000/api/login', body);
    if(responce.data.token){
      localStorage.setItem("token", responce.data.token);
      history.push('/dashboard');
    } else {
        setError(true)
        errorDiv = <Alert severity="error">Please enter correct email or password</Alert>
    }

}

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      {errorDiv}
      { error ? <Alert severity="error">Please enter correct email or password</Alert> : ''}
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography className={classes.h} component="h1" variant="h5">
          Sign in
        </Typography>
        <form className={classes.form} noValidate>
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="email"
            label="Email Address"
            name="email"
            inputRef={email}
            autoComplete="email"
            autoFocus
          />
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="password"
            inputRef={password}
            label="Password"
            type="password"
            id="password"
            autoComplete="current-password"
          />
          <Button
            type="button"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
            onClick={getValues}
          >
            Sign In
          </Button>
          <Grid container>
            <Grid item xs>
              <Link href="#" variant="body2">
                Forgot password?
              </Link>
            </Grid>
            <Grid item>
              {/* <Link variant="body2"> */}
                  <Page to="/singup">

                    {"Don't have an account? Sign Up"}
                  </Page>
              {/* </Link> */}
            </Grid>
          </Grid>
        </form>
      </div>
    </Container>
  );
}
